// Fetch API Example - Weather from Yr.no
const fetchBtn = componentRoot.querySelector('.fetch-btn');
const dataDisplay = componentRoot.querySelector('.data-display');

if (fetchBtn && dataDisplay) {
  fetchBtn.addEventListener('click', async function() {
    dataDisplay.innerHTML = '<p style="color: #666;">Getting your location...</p>';

    // Try to get user's location, fallback to Oslo if not available
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async function(position) {
          const lat = position.coords.latitude;
          const lon = position.coords.longitude;
          await fetchWeather(lat, lon, 'Your Location');
        },
        async function(error) {
          console.log('Location error, using Oslo as fallback:', error.message);
          dataDisplay.innerHTML = '<p style="color: #ffc107;">Could not get your location. Showing Oslo weather...</p>';
          setTimeout(() => fetchWeather(59.91, 10.75, 'Oslo, Norway'), 1000);
        }
      );
    } else {
      dataDisplay.innerHTML = '<p style="color: #ffc107;">Geolocation not supported. Showing Oslo weather...</p>';
      setTimeout(() => fetchWeather(59.91, 10.75, 'Oslo, Norway'), 1000);
    }
  });

  async function fetchWeather(lat, lon, locationName) {
    try {
      dataDisplay.innerHTML = '<p style="color: #666;">Loading weather data...</p>';

      const response = await fetch(`https://api.met.no/weatherapi/locationforecast/2.0/compact?lat=${lat.toFixed(2)}&lon=${lon.toFixed(2)}`, {
        headers: {
          'User-Agent': 'DesignIT-WebStudio/1.0'
        }
      });

      if (!response.ok) {
        throw new Error('Failed to fetch weather data');
      }

      const data = await response.json();
      const current = data.properties.timeseries[0];
      const details = current.data.instant.details;
      const next1h = current.data.next_1_hours;

      const temp = details.air_temperature;
      const windSpeed = details.wind_speed;
      const humidity = details.relative_humidity;
      const symbolCode = next1h ? next1h.summary.symbol_code : 'unknown';

      // Weather emoji mapping
      const weatherEmoji = {
        'clearsky': '‚òÄÔ∏è',
        'fair': 'üå§Ô∏è',
        'partlycloudy': '‚õÖ',
        'cloudy': '‚òÅÔ∏è',
        'rain': 'üåßÔ∏è',
        'lightrain': 'üå¶Ô∏è',
        'heavyrain': '‚õàÔ∏è',
        'snow': 'üå®Ô∏è',
        'fog': 'üå´Ô∏è'
      };

      const emoji = Object.keys(weatherEmoji).find(key => symbolCode.includes(key)) || 'default';
      const icon = weatherEmoji[emoji] || 'üå°Ô∏è';

      dataDisplay.innerHTML = `
        <div style="text-align: center; padding: 20px; background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); border-radius: 8px; color: white;">
          <div style="font-size: 3em; margin-bottom: 10px;">${icon}</div>
          <h3 style="margin: 0 0 5px 0; font-size: 1.5em;">${locationName}</h3>
          <div style="font-size: 0.8em; opacity: 0.9; margin-bottom: 10px;">${lat.toFixed(4)}, ${lon.toFixed(4)}</div>
          <div style="font-size: 2.5em; font-weight: bold; margin: 15px 0;">${temp}¬∞C</div>
          <div style="display: flex; justify-content: center; gap: 20px; font-size: 0.9em; opacity: 0.95;">
            <div>üí® ${windSpeed} m/s</div>
            <div>üíß ${humidity}%</div>
          </div>
        </div>
        <div style="margin-top: 10px; font-size: 0.85em; color: #666; text-align: center;">
          Data from <a href="https://www.yr.no" target="_blank" style="color: #667eea;">Yr.no</a> (MET Norway)
        </div>
      `;
    } catch (error) {
      dataDisplay.innerHTML = '<p style="color: red;">Error: ' + error.message + '</p>';
    }
  }
}

