// Clipboard API Example
const clipboardText = componentRoot.querySelector('.clipboard-text');
const copyBtn = componentRoot.querySelector('.copy-clip-btn');
const pasteBtn = componentRoot.querySelector('.paste-clip-btn');
const clipboardStatus = componentRoot.querySelector('.clipboard-status');

if (copyBtn && pasteBtn) {
  copyBtn.addEventListener('click', async function() {
    try {
      await navigator.clipboard.writeText(clipboardText.value);
      clipboardStatus.innerHTML = '<p style="color: #28a745;">‚úì Copied to clipboard!</p>';
      setTimeout(() => clipboardStatus.innerHTML = '', 2000);
    } catch (err) {
      clipboardStatus.innerHTML = '<p style="color: red;">Failed to copy: ' + err.message + '</p>';
    }
  });

  pasteBtn.addEventListener('click', async function() {
    try {
      const text = await navigator.clipboard.readText();
      clipboardText.value = text;
      clipboardStatus.innerHTML = '<p style="color: #667eea;">‚úì Pasted from clipboard!</p>';
      setTimeout(() => clipboardStatus.innerHTML = '', 2000);
    } catch (err) {
      clipboardStatus.innerHTML = '<p style="color: red;">Failed to paste: ' + err.message + '</p>';
    }
  });
}

