// Web Notifications API Example
const requestBtn = componentRoot.querySelector('.request-permission-btn');
const sendBtn = componentRoot.querySelector('.send-notification-btn');
const notificationStatus = componentRoot.querySelector('.notification-status');

if (requestBtn && sendBtn) {
  // Check initial permission status
  if (Notification.permission === 'granted') {
    notificationStatus.innerHTML = '<p style="color: #28a745;">‚úì Notifications enabled</p>';
  } else if (Notification.permission === 'denied') {
    notificationStatus.innerHTML = '<p style="color: #dc3545;">Notifications blocked. Please enable in browser settings.</p>';
  }

  requestBtn.addEventListener('click', async function() {
    if (!('Notification' in window)) {
      notificationStatus.innerHTML = '<p style="color: red;">This browser does not support notifications</p>';
      return;
    }

    const permission = await Notification.requestPermission();

    if (permission === 'granted') {
      notificationStatus.innerHTML = '<p style="color: #28a745;">‚úì Notifications enabled!</p>';
    } else {
      notificationStatus.innerHTML = '<p style="color: #dc3545;">Notification permission denied</p>';
    }
  });

  sendBtn.addEventListener('click', function() {
    if (Notification.permission === 'granted') {
      new Notification('Hello from DesignIT! üëã', {
        body: 'This is a test notification from your web app.',
        icon: 'https://via.placeholder.com/128/667eea/ffffff?text=DIT',
        badge: 'https://via.placeholder.com/96/667eea/ffffff?text=!'
      });
      notificationStatus.innerHTML = '<p style="color: #667eea;">‚úì Notification sent!</p>';
    } else {
      notificationStatus.innerHTML = '<p style="color: #ffc107;">Please enable notifications first!</p>';
    }
  });
}

