// Battery Status API Example
const batteryBtn = componentRoot.querySelector('.battery-btn');
const batteryDisplay = componentRoot.querySelector('.battery-display');

if (batteryBtn && batteryDisplay) {
  batteryBtn.addEventListener('click', async function() {
    if (!('getBattery' in navigator)) {
      batteryDisplay.innerHTML = '<p style="color: red;">Battery API not supported in this browser</p>';
      return;
    }

    try {
      const battery = await navigator.getBattery();
      const level = (battery.level * 100).toFixed(0);
      const charging = battery.charging ? 'Yes ‚ö°' : 'No';
      const chargingTime = battery.chargingTime === Infinity ? 'N/A' : battery.chargingTime + ' seconds';
      const dischargingTime = battery.dischargingTime === Infinity ? 'N/A' : (battery.dischargingTime / 60).toFixed(0) + ' minutes';

      batteryDisplay.innerHTML = `
        <h4 style="margin-top: 0;">üîã Battery Information</h4>
        <p><strong>Level:</strong> ${level}%</p>
        <p><strong>Charging:</strong> ${charging}</p>
        <p><strong>Time to charge:</strong> ${chargingTime}</p>
        <p><strong>Time to discharge:</strong> ${dischargingTime}</p>
      `;
    } catch (error) {
      batteryDisplay.innerHTML = '<p style="color: red;">Error: ' + error.message + '</p>';
    }
  });
}

