// Canvas API Example
const canvas = componentRoot.querySelector('.draw-canvas');
const ctx = canvas ? canvas.getContext('2d') : null;
const circleBtn = componentRoot.querySelector('.draw-circle-btn');
const rectBtn = componentRoot.querySelector('.draw-rect-btn');
const textBtn = componentRoot.querySelector('.draw-text-btn');
const clearBtn = componentRoot.querySelector('.clear-canvas-btn');

if (ctx && circleBtn && rectBtn && textBtn && clearBtn) {
  // Set initial canvas background
  ctx.fillStyle = '#f9f9f9';
  ctx.fillRect(0, 0, canvas.width, canvas.height);

  circleBtn.addEventListener('click', function() {
    const x = Math.random() * (canvas.width - 100) + 50;
    const y = Math.random() * (canvas.height - 100) + 50;
    const radius = Math.random() * 40 + 20;
    const hue = Math.random() * 360;

    ctx.beginPath();
    ctx.arc(x, y, radius, 0, Math.PI * 2);
    ctx.fillStyle = `hsl(${hue}, 70%, 60%)`;
    ctx.fill();
    ctx.strokeStyle = `hsl(${hue}, 70%, 40%)`;
    ctx.lineWidth = 3;
    ctx.stroke();
  });

  rectBtn.addEventListener('click', function() {
    const x = Math.random() * (canvas.width - 150) + 25;
    const y = Math.random() * (canvas.height - 100) + 25;
    const width = Math.random() * 100 + 50;
    const height = Math.random() * 80 + 40;
    const hue = Math.random() * 360;

    ctx.fillStyle = `hsl(${hue}, 70%, 60%)`;
    ctx.fillRect(x, y, width, height);
    ctx.strokeStyle = `hsl(${hue}, 70%, 40%)`;
    ctx.lineWidth = 3;
    ctx.strokeRect(x, y, width, height);
  });

  textBtn.addEventListener('click', function() {
    const messages = ['Hello Canvas!', 'DesignIT', 'Web APIs üé®', 'HTML5 Canvas'];
    const message = messages[Math.floor(Math.random() * messages.length)];
    const x = Math.random() * (canvas.width - 200) + 50;
    const y = Math.random() * (canvas.height - 50) + 40;
    const hue = Math.random() * 360;

    ctx.font = 'bold 32px Arial';
    ctx.fillStyle = `hsl(${hue}, 70%, 50%)`;
    ctx.fillText(message, x, y);
  });

  clearBtn.addEventListener('click', function() {
    ctx.fillStyle = '#f9f9f9';
    ctx.fillRect(0, 0, canvas.width, canvas.height);
  });
}

