// IntersectionObserver API Example
const lazyItems = componentRoot.querySelectorAll('.lazy-item');

if (lazyItems.length > 0 && 'IntersectionObserver' in window) {
  const observerOptions = {
    root: componentRoot.querySelector('.observer-scroll-area'),
    rootMargin: '0px',
    threshold: 0.1
  };

  const observer = new IntersectionObserver(function(entries) {
    entries.forEach(entry => {
      if (entry.isIntersecting) {
        const item = entry.target;
        const index = item.getAttribute('data-index');
        const placeholder = item.querySelector('.lazy-placeholder');

        // Simulate image loading
        setTimeout(() => {
          const colors = ['#667eea', '#764ba2', '#f093fb', '#4facfe', '#43e97b', '#fa709a'];
          const color = colors[(index - 1) % colors.length];

          placeholder.innerHTML = `
            <div style="background: linear-gradient(135deg, ${color} 0%, ${color}dd 100%); height: 100%; display: flex; align-items: center; justify-content: center; color: white; font-size: 24px; font-weight: bold; border-radius: 8px; padding: 30px; box-sizing: border-box;">
              ‚úì Image ${index} Loaded
            </div>
          `;
          placeholder.classList.add('loaded');
        }, 300);

        // Stop observing this item
        observer.unobserve(item);
      }
    });
  }, observerOptions);

  // Start observing all lazy items
  lazyItems.forEach(item => observer.observe(item));
} else if (lazyItems.length > 0) {
  // Fallback for browsers without IntersectionObserver
  componentRoot.querySelector('.observer-container').innerHTML += '<p style="color: red; margin-top: 10px;">IntersectionObserver not supported in this browser</p>';
}

