// Drag and Drop API Example
const draggableItems = componentRoot.querySelectorAll('.draggable-item');
const dropArea = componentRoot.querySelector('.drop-area');

if (draggableItems.length > 0 && dropArea) {
  let draggedItem = null;

  draggableItems.forEach(item => {
    item.addEventListener('dragstart', function(e) {
      draggedItem = this;
      this.style.opacity = '0.5';
      e.dataTransfer.effectAllowed = 'move';
      e.dataTransfer.setData('text/html', this.innerHTML);
    });

    item.addEventListener('dragend', function() {
      this.style.opacity = '1';
    });
  });

  dropArea.addEventListener('dragover', function(e) {
    e.preventDefault();
    e.dataTransfer.dropEffect = 'move';
    this.style.borderColor = '#667eea';
    this.style.backgroundColor = '#f0f4ff';
  });

  dropArea.addEventListener('dragleave', function() {
    this.style.borderColor = '#ddd';
    this.style.backgroundColor = '#fff';
  });

  dropArea.addEventListener('drop', function(e) {
    e.preventDefault();

    if (draggedItem) {
      const itemName = draggedItem.getAttribute('data-item');
      const droppedItem = document.createElement('div');
      droppedItem.className = 'dropped-item';
      droppedItem.innerHTML = draggedItem.innerHTML;
      droppedItem.style.cssText = 'background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 12px; margin: 8px 0; border-radius: 5px; font-weight: 600;';

      this.appendChild(droppedItem);

      // Remove 'Drop items here' text if it exists
      if (this.textContent.includes('Drop items here')) {
        this.innerHTML = '';
        this.appendChild(droppedItem);
      }
    }

    this.style.borderColor = '#28a745';
    this.style.backgroundColor = '#f0fff4';

    setTimeout(() => {
      this.style.borderColor = '#ddd';
      this.style.backgroundColor = '#fff';
    }, 500);
  });
}

