// Page Visibility API Example
const visibilityStatus = componentRoot.querySelector('.visibility-status');
const switchCountElement = componentRoot.querySelector('.switch-count');

if (visibilityStatus && switchCountElement && 'hidden' in document) {
  let switchCount = 0;

  function updateVisibilityStatus() {
    if (document.hidden) {
      visibilityStatus.innerHTML = '<span style="color: #dc3545; font-size: 18px; font-weight: bold;">üî¥ Tab is Hidden</span>';
      visibilityStatus.style.padding = '15px';
      visibilityStatus.style.backgroundColor = '#ffe6e6';
      visibilityStatus.style.borderRadius = '5px';
    } else {
      visibilityStatus.innerHTML = '<span style="color: #28a745; font-size: 18px; font-weight: bold;">üü¢ Tab is Visible</span>';
      visibilityStatus.style.padding = '15px';
      visibilityStatus.style.backgroundColor = '#e6ffe6';
      visibilityStatus.style.borderRadius = '5px';
      switchCount++;
      switchCountElement.textContent = switchCount;
    }
  }

  // Initial check
  updateVisibilityStatus();

  // Listen for visibility changes
  document.addEventListener('visibilitychange', updateVisibilityStatus);
} else if (visibilityStatus) {
  visibilityStatus.innerHTML = '<span style="color: red;">Page Visibility API not supported</span>';
}

