CSS:
/* Abbreviation or Abbr */ 
abbr[title] {
  text-decoration: dotted underline;
  cursor: help;
}

/* Anchor <a> */
a {
  color: #0066cc;
  text-decoration: none;
}

a:hover {
  text-decoration: underline;
}

/* Article */
article {
  padding: 1rem;
  margin: 1rem 0;
  border: 1px solid #ccc;
  background-color: #f5f5f5;
}

article > article {
  padding: 0.8rem;
  margin: 0.5rem 0;
  border-left: 3px solid #007bff;
  background-color: white;
}

article h2 {
  margin-top: 0;
  color: #333;
}

article h3 {
  margin-top: 0;
  color: #007bff;
  font-size: 1.1rem;
}

article p {
  margin: 0.5rem 0 0 0;
  color: #666;
}

/* Blockquote or Blockquote */ 
blockquote {
  border-left: 4px solid #ccc;
  margin: 1.5rem 10px;
  padding-left: 10px;
  color: #666;
  font-style: italic;
}

blockquote footer {
  text-align: right;
  font-style: normal;
  color: #999;
  margin-top: 0.5rem;
}

/* Button */
button {
  padding: 0.75rem 1.5rem;
  font-size: 1rem;
  font-weight: 500;
  border-radius: 8px;
  border: 2px solid transparent;
  cursor: pointer;
  transition: all 0.3s ease;
  margin: 0.25rem;
}

.btn-primary {
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  box-shadow: 0 4px 15px rgba(102, 126, 234, 0.4);
}

.btn-primary:hover {
  transform: translateY(-2px);
  box-shadow: 0 6px 20px rgba(102, 126, 234, 0.6);
}

.btn-secondary {
  background: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
  color: white;
  box-shadow: 0 4px 15px rgba(245, 87, 108, 0.4);
}

.btn-secondary:hover {
  transform: translateY(-2px);
  box-shadow: 0 6px 20px rgba(245, 87, 108, 0.6);
}

.btn-outline {
  background: transparent;
  color: #667eea;
  border-color: #667eea;
}

.btn-outline:hover {
  background: #667eea;
  color: white;
  transform: translateY(-2px);
}

/* Code or Code */ 
code {
  background-color: #f4f4f4;
  border: 1px solid #ddd;
  border-radius: 3px;
  font-family: 'Courier New', Courier, monospace;
  padding: 2px 4px;
}

/* Colgroup */
table {
  border-collapse: collapse;
  width: 100%;
}

th,
td {
  border: 1px solid #ddd;
  padding: 10px;
  text-align: left;
}

.product-info {
  background-color: #e3f2fd;
}

.pricing {
  background-color: #fff3e0;
}

/* Column */
table {
  border-collapse: collapse;
  width: 100%;
}

th,
td {
  border: 1px solid #ddd;
  padding: 10px;
}

.col-name {
  background-color: #ffebee;
  width: 40%;
}

.col-status {
  background-color: #e8f5e9;
  width: 30%;
}

.col-progress {
  background-color: #fff9c4;
  width: 30%;
}

.progress-cell {
  display: flex;
  align-items: center;
  gap: 10px;
}

progress {
  flex: 1;
  height: 20px;
}

.progress-text {
  min-width: 45px;
  font-weight: bold;
  color: #333;
  font-size: 0.9rem;
}

progress::-webkit-progress-bar {
  background-color: #e0e0e0;
  border-radius: 10px;
}

progress::-webkit-progress-value {
  background: linear-gradient(90deg, #4caf50, #8bc34a);
  border-radius: 10px;
  transition: width 0.3s ease;
}

/* Datalist */
label {
  display: block;
  margin-bottom: 10px;
  font-weight: 500;
}

input {
  padding: 8px 12px;
  border: 2px solid #ddd;
  border-radius: 5px;
  font-size: 1rem;
  width: 250px;
  transition: border-color 0.3s ease;
}

input:focus {
  outline: none;
  border-color: #667eea;
}

/* Deleted Text or Del */
del {
  text-decoration: line-through;
  background-color: #fbb;
  color: #555;
}

ins {
  text-decoration: none;
  background-color: #d4fcbc;
}

/* Details */
details {
  border: 1px solid #aaa;
  border-radius: 4px;
  padding: 0.5em 0.5em 0;
}

summary {
  font-weight: bold;
  margin: -0.5em -0.5em 0;
  padding: 0.5em;
}

details[open] {
  padding: 0.5em;
}

details[open] summary {
  border-bottom: 1px solid #aaa;
  margin-bottom: 0.5em;
}

/* Fieldset */
fieldset {
  border: 2px solid #ccc;
  border-radius: 4px;
  padding: 1rem;
  margin: 1rem 0;
}

legend {
  background-color: #333;
  color: #fff;
  padding: 4px 8px;
  border-radius: 3px;
}

label {
  display: block;
  margin-top: 0.5rem;
}

input {
  display: block;
  margin-bottom: 0.5rem;
  padding: 0.3rem;
}

/* Footer */
footer {
  background-color: #fff;
  color: #333;
  padding: 1rem;
  text-align: center;
  margin-top: 2rem;
  border-top: 1px solid #ddd;
}

footer p {
  margin: 0.5rem 0;
}

/* Form */
form.form-example {
  display: table;
}

div.form-example {
  display: table-row;
}

input {
  display: table-cell;
  margin-bottom: 10px;
  margin-left: 10px;
}

/* Header */
header {
  background-color: #fff;
  color: #333;
  padding: 1rem;
  border-bottom: 1px solid #ddd;
}

.logo-container {
  display: flex;
  justify-content: space-between;
  align-items: center;
  max-width: 1200px;
  margin: 0 auto;
}

.logo {
  margin: 0;
  font-size: 1.5rem;
  font-weight: bold;
  color: #333;
}

header nav {
  display: flex;
  gap: 1.5rem;
}

header nav a {
  color: #333;
  text-decoration: none;
  font-weight: 500;
  transition: color 0.3s ease;
}

header nav a:hover {
  color: #666;
}

/* Header Group */
hgroup {
  margin-bottom: 1.5rem;
  padding-bottom: 0.5rem;
  border-bottom: 2px solid #ddd;
}

hgroup h1 {
  margin: 0 0 0.5rem 0;
  color: #333;
  font-size: 2rem;
  font-weight: bold;
}

hgroup p {
  margin: 0;
  color: #666;
  font-size: 1.1rem;
  font-weight: normal;
}

article {
  max-width: 800px;
  margin: 2rem auto;
  padding: 1.5rem;
}

/* Horizontal rule */
hr {
  border: none;
  border-top: 1px solid #ddd;
  margin: 2rem 0;
}

/* Iframe */
iframe {
  width: 100%;
  height: 400px;
  border: 1px solid #ddd;
  border-radius: 4px;
}

/* Input type text */
label {
  font-weight: 600;
  color: #2c3e50;
  font-size: 0.9rem;
  display: block;
  margin-bottom: 0.25rem;
}

input[type="text"] {
  display: block;
  padding: 0.25rem 0.5rem;
  font-size: 1rem;
}

/* Input type number */
label {
  font-weight: 600;
  color: #2c3e50;
  font-size: 0.9rem;
  display: block;
  margin-bottom: 0.25rem;
}

input[type="number"] {
  display: block;
  padding: 0.15rem 0.4rem;
  font-size: 1rem;
}

/* Input type date */
label {
  font-weight: 600;
  color: #2c3e50;
  font-size: 0.9rem;
  display: block;
  margin-bottom: 0.25rem;
}

input[type="date"] {
  display: block;
  padding: 0.15rem 0.4rem;
  font-size: 1rem;
  cursor: pointer;
}

/* Input type time */
label {
  font-weight: 600;
  color: #2c3e50;
  font-size: 0.9rem;
  display: block;
  margin-bottom: 0.25rem;
}

input[type="time"] {
  display: block;
  padding: 0.15rem 0.4rem;
  font-size: 1rem;
  cursor: pointer;
}

/* Input type color */
label {
  font-weight: 600;
  color: #2c3e50;
  font-size: 0.9rem;
  display: block;
  margin-bottom: 0.25rem;
}

input[type="color"] {
  display: block;
  padding: 0.05rem;
  width: 50px;
  height: 25px;
  border: none;
  border-radius: 4px;
  cursor: pointer;
}

/* Input type range */
label {
  font-weight: 600;
  color: #2c3e50;
  font-size: 0.9rem;
  display: block;
  margin-bottom: 0.25rem;
}

input[type="range"] {
  display: inline-block;
  cursor: pointer;
  vertical-align: middle;
}

#rangeValue {
  font-weight: bold;
  min-width: 3ch;
  display: inline-block;
  font-size: 1rem;
}

/* Input type file */
label {
  font-weight: 600;
  color: #2c3e50;
  font-size: 0.9rem;
  display: block;
  margin-bottom: 0.25rem;
}

input[type="file"] {
  display: block;
  font-size: 1rem;
  cursor: pointer;
}

/* Input type checkbox */
label {
  font-weight: 600;
  color: #2c3e50;
  font-size: 0.9rem;
}

input[type="checkbox"] {
  cursor: pointer;
}

input[type="checkbox"] + label {
  cursor: pointer;
  font-weight: normal;
}

/* Input type radio */
label {
  font-weight: 600;
  color: #2c3e50;
  font-size: 0.9rem;
}

input[type="radio"] {
  cursor: pointer;
}

input[type="radio"] + label {
  cursor: pointer;
  font-weight: normal;
}

/* Input type="datetime-local" */ 
input[type="datetime-local"] {
  display: inline-block;
  padding: 0.15rem 0.4rem;
  font-size: 1rem;
  vertical-align: middle;
}

label[for="meeting-time"] {
  display: block;
  margin-bottom: 0.4rem;
}

/* Input type="email" */ 
input[type="email"] {
  display: inline-block;
  padding: 0.15rem 0.4rem;
  font-size: 1rem;
  vertical-align: middle;
}

label[for="email"] {
  display: block;
  margin-bottom: 0.4rem;
}

/* Input type="password" */ 
input[type="password"],
input[name="username"] {
  display: inline-block;
  padding: 0.15rem 0.4rem;
  font-size: 1rem;
  vertical-align: middle;
}

label[for="username"],
label[for="pass"] {
  display: block;
  margin-top: 0.5rem;
}

input[type='submit'] {
  margin-top: 1rem;
}

/* Input type="search" */ 
input[type="search"] {
  display: inline-block;
  padding: 0.15rem 0.4rem;
  font-size: 1rem;
  vertical-align: middle;
}

label[for="site-search"] {
  display: block;
  margin-bottom: 0.4rem;
}

/* Keyboard Input or Kbd */ 
kbd {
  background-color: #eee;
  border-radius: 3px;
  border: 1px solid #b4b4b4;
  box-shadow:
    0 1px 1px rgba(0, 0, 0, 0.2),
    0 2px 0 0 rgba(255, 255, 255, 0.7) inset;
  color: #333;
  display: inline-block;
  font-size: 0.85em;
  font-weight: 700;
  line-height: 1;
  padding: 2px 4px;
  white-space: nowrap;
}

/* Label */ 
label {
  display: block;
  margin-bottom: 0.5rem;
  font-weight: 500;
}

input {
  padding: 0.5rem;
  font-size: 1rem;
}

/* Nav */
nav.component-nav {
  display: flex;
  gap: 1.5rem;
  padding: 1rem;
  background-color: #f5f5f5;
}

nav.component-nav a {
  color: #333;
  text-decoration: none;
  font-weight: 500;
  transition: color 0.3s;
}

nav.component-nav a:hover {
  color: #667eea;
}

/* Option group or Optgroup */ 
label {
  display: block;
  margin-bottom: 0.5rem;
  font-weight: 500;
}

select {
  padding: 0.5rem;
  font-size: 1rem;
}

/* Ordered list */
ol {
  padding-left: 1.5rem;
  margin: 1rem 0;
}

ol li {
  margin-bottom: 0.5rem;
  line-height: 1.5;
}

/* Unordered list */
ul {
  padding-left: 1.5rem;
  margin: 1rem 0;
}

ul li {
  margin-bottom: 0.5rem;
  line-height: 1.5;
}

/* Progress */
label {
  padding-right: 10px;
  font-size: 1rem;
}

/* Select */
label {
  font-family: sans-serif;
  font-size: 1rem;
  padding-right: 10px;
}

select {
  font-size: 0.9rem;
  padding: 2px 5px;
}

/* Audio */
audio {
  width: 100%;
  max-width: 400px;
}

/* Picture */
picture {
  display: block;
}

picture img {
  max-width: 100%;
  height: auto;
  border: 2px solid #ddd;
  border-radius: 4px;
}

/* Video */
video {
  max-width: 100%;
  height: auto;
  border: 2px solid #ddd;
  border-radius: 4px;
}

/* Subscript or Sub */ 
sub {
  font-size: 0.8em;
}

/* Superscript or Sup */ 
sup {
  font-size: 0.8em;
}

/* Table */ 
table {
  border-collapse: collapse;
  width: 100%;
}

th,
td {
  border: 1px solid #ddd;
  padding: 8px;
  text-align: left;
}

thead th[colspan] {
  background-color: #333;
  color: white;
  text-align: center;
}

thead th:not([colspan]) {
  background-color: #f5f5f5;
}

tfoot {
  background-color: #f5f5f5;
  font-weight: bold;
}

tfoot th[colspan] {
  text-align: center;
}

tbody tr:hover {
  background-color: #f9f9f9;
}

/* Textarea */ 
textarea {
  padding: 10px;
  max-width: 100%;
  line-height: 1.5;
  border-radius: 5px;
  border: 1px solid #ccc;
  box-shadow: 1px 1px 1px #999;
  font-size: 1rem;
}
