// Geolocation API Example
const geoBtn = componentRoot.querySelector('.geo-btn');
const geoDisplay = componentRoot.querySelector('.geo-display');

if (geoBtn && geoDisplay) {
  geoBtn.addEventListener('click', function() {
    if (!navigator.geolocation) {
      geoDisplay.innerHTML = '<p style="color: red;">Geolocation is not supported by your browser</p>';
      return;
    }

    geoDisplay.innerHTML = '<p style="color: #666;">Locating...</p>';

    navigator.geolocation.getCurrentPosition(
      function(position) {
        const lat = position.coords.latitude.toFixed(4);
        const lon = position.coords.longitude.toFixed(4);
        const accuracy = position.coords.accuracy.toFixed(0);

        geoDisplay.innerHTML = `
          <h4 style="margin-top: 0;">üìç Your Location</h4>
          <p><strong>Latitude:</strong> ${lat}</p>
          <p><strong>Longitude:</strong> ${lon}</p>
          <p><strong>Accuracy:</strong> ¬±${accuracy} meters</p>
          <iframe 
            width="100%" 
            height="300" 
            frameborder="0" 
            scrolling="no" 
            marginheight="0" 
            marginwidth="0" 
            src="https://www.openstreetmap.org/export/embed.html?bbox=${parseFloat(lon)-0.01},${parseFloat(lat)-0.01},${parseFloat(lon)+0.01},${parseFloat(lat)+0.01}&layer=mapnik&marker=${lat},${lon}" 
            style="border: 1px solid #ddd; border-radius: 5px; margin-top: 10px;">
          </iframe>
          <a href="https://www.openstreetmap.org/?mlat=${lat}&mlon=${lon}&zoom=15" target="_blank" style="color: #667eea; display: block; margin-top: 8px;">View Larger Map ‚Üí</a>
        `;
      },
      function(error) {
        geoDisplay.innerHTML = '<p style="color: red;">Error: ' + error.message + '</p>';
      }
    );
  });
}

