Web APIs.JS:
// LocalStorage API Example
const storageInput = componentRoot.querySelector('.storage-input');
const saveBtn = componentRoot.querySelector('.save-btn');
const loadBtn = componentRoot.querySelector('.load-btn');
const clearBtn = componentRoot.querySelector('.clear-btn');
const storageDisplay = componentRoot.querySelector('.storage-display');

if (saveBtn && loadBtn && clearBtn) {
  saveBtn.addEventListener('click', function() {
    const note = storageInput.value;
    if (note) {
      localStorage.setItem('myNote', note);
      storageDisplay.innerHTML = '<p style="color: #28a745;">‚úì Note saved!</p>';
    }
  });

  loadBtn.addEventListener('click', function() {
    const note = localStorage.getItem('myNote');
    if (note) {
      storageInput.value = note;
      storageDisplay.innerHTML = '<p style="color: #667eea;">‚úì Note loaded!</p>';
    } else {
      storageDisplay.innerHTML = '<p style="color: #666;">No saved note found.</p>';
    }
  });

  clearBtn.addEventListener('click', function() {
    localStorage.removeItem('myNote');
    storageInput.value = '';
    storageDisplay.innerHTML = '<p style="color: #dc3545;">‚úì Note cleared!</p>';
  });
}

