// setTimeout/setInterval Example
const countdownDisplay = componentRoot.querySelector('.countdown');
const counterDisplay = componentRoot.querySelector('.counter');
const startCountdownBtn = componentRoot.querySelector('.start-countdown-btn');
const startCounterBtn = componentRoot.querySelector('.start-counter-btn');
const stopAllBtn = componentRoot.querySelector('.stop-all-btn');

let countdownTimer = null;
let counterInterval = null;

if (startCountdownBtn && startCounterBtn && stopAllBtn) {
  startCountdownBtn.addEventListener('click', function() {
    if (countdownTimer) clearTimeout(countdownTimer);

    let seconds = 10;
    countdownDisplay.textContent = seconds;

    function countdown() {
      seconds--;
      countdownDisplay.textContent = seconds;

      if (seconds > 0) {
        countdownTimer = setTimeout(countdown, 1000);
      } else {
        countdownDisplay.textContent = 'Done!';
      }
    }

    countdownTimer = setTimeout(countdown, 1000);
  });

  startCounterBtn.addEventListener('click', function() {
    if (counterInterval) clearInterval(counterInterval);

    let count = 0;
    counterDisplay.textContent = count;

    counterInterval = setInterval(function() {
      count++;
      counterDisplay.textContent = count;
    }, 1000);
  });

  stopAllBtn.addEventListener('click', function() {
    if (countdownTimer) {
      clearTimeout(countdownTimer);
      countdownTimer = null;
    }
    if (counterInterval) {
      clearInterval(counterInterval);
      counterInterval = null;
    }
  });
}

