// Promise Example
const fetchDataBtn = componentRoot.querySelector('.fetch-data-btn');
const fetchErrorBtn = componentRoot.querySelector('.fetch-error-btn');
const statusDisplay = componentRoot.querySelector('.promise-status');

function simulateAPICall(shouldSucceed) {
  return new Promise((resolve, reject) => {
    statusDisplay.innerHTML = '<p style="color: #667eea;">‚è≥ Loading...</p>';

    setTimeout(() => {
      if (shouldSucceed) {
        resolve({
          id: 123,
          name: 'Sample Data',
          timestamp: new Date().toLocaleTimeString()
        });
      } else {
        reject(new Error('Failed to fetch data'));
      }
    }, 2000);
  });
}

if (fetchDataBtn && fetchErrorBtn) {
  fetchDataBtn.addEventListener('click', function() {
    simulateAPICall(true)
      .then(data => {
        statusDisplay.innerHTML = `
          <div style="color: #28a745; padding: 15px; background-color: #d4edda; border-radius: 5px;">
            <h4 style="margin: 0 0 10px 0;">‚úì Success!</h4>
            <p style="margin: 5px 0;"><strong>ID:</strong> ${data.id}</p>
            <p style="margin: 5px 0;"><strong>Name:</strong> ${data.name}</p>
            <p style="margin: 5px 0;"><strong>Time:</strong> ${data.timestamp}</p>
          </div>
        `;
      })
      .catch(error => {
        statusDisplay.innerHTML = `<p style="color: #dc3545;">‚úó Error: ${error.message}</p>`;
      });
  });

  fetchErrorBtn.addEventListener('click', function() {
    simulateAPICall(false)
      .then(data => {
        statusDisplay.innerHTML = `<p style="color: #28a745;">‚úì Success: ${JSON.stringify(data)}</p>`;
      })
      .catch(error => {
        statusDisplay.innerHTML = `
          <div style="color: #dc3545; padding: 15px; background-color: #f8d7da; border-radius: 5px;">
            <h4 style="margin: 0 0 10px 0;">‚úó Error Caught!</h4>
            <p style="margin: 5px 0;">${error.message}</p>
          </div>
        `;
      });
  });
}

