// Form Validation Example
const validationForm = componentRoot.querySelector('.validation-form');
const emailInput = componentRoot.querySelector('.val-email');
const passwordInput = componentRoot.querySelector('.val-password');
const ageInput = componentRoot.querySelector('.val-age');
const validationResult = componentRoot.querySelector('.validation-result');

if (validationForm) {
  function showError(input, message) {
    const formGroup = input.parentElement;
    const errorMsg = formGroup.querySelector('.error-msg');
    errorMsg.textContent = message;
    errorMsg.style.display = 'block';
    input.style.borderColor = '#dc3545';
  }

  function clearError(input) {
    const formGroup = input.parentElement;
    const errorMsg = formGroup.querySelector('.error-msg');
    errorMsg.textContent = '';
    errorMsg.style.display = 'none';
    input.style.borderColor = '#28a745';
  }

  function validateEmail(email) {
    const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return re.test(email);
  }

  validationForm.addEventListener('submit', function(e) {
    e.preventDefault();
    let isValid = true;

    // Validate email
    if (!emailInput.value.trim()) {
      showError(emailInput, 'Email is required');
      isValid = false;
    } else if (!validateEmail(emailInput.value)) {
      showError(emailInput, 'Please enter a valid email');
      isValid = false;
    } else {
      clearError(emailInput);
    }

    // Validate password
    if (!passwordInput.value) {
      showError(passwordInput, 'Password is required');
      isValid = false;
    } else if (passwordInput.value.length < 8) {
      showError(passwordInput, 'Password must be at least 8 characters');
      isValid = false;
    } else {
      clearError(passwordInput);
    }

    // Validate age
    const age = parseInt(ageInput.value);
    if (!ageInput.value) {
      showError(ageInput, 'Age is required');
      isValid = false;
    } else if (age < 18 || age > 100) {
      showError(ageInput, 'Age must be between 18 and 100');
      isValid = false;
    } else {
      clearError(ageInput);
    }

    if (isValid) {
      validationResult.innerHTML = `
        <div style="padding: 15px; background-color: #d4edda; color: #28a745; border-radius: 5px; margin-top: 15px;">
          <h4 style="margin: 0 0 10px 0;">‚úì Form Valid!</h4>
          <p style="margin: 5px 0;">Email: ${emailInput.value}</p>
          <p style="margin: 5px 0;">Age: ${ageInput.value}</p>
        </div>
      `;
    }
  });
}

