// Language Preference Cookie Example
const langSelect = componentRoot.querySelector('.lang-select');
const cookieValue = componentRoot.querySelector('.cookie-value');
const pageContent = componentRoot.querySelector('.page-content');

const languageNames = {
  'en': 'English',
  'no': 'Norwegian (Norsk)',
  'es': 'Spanish (Espa√±ol)',
  'fr': 'French (Fran√ßais)',
  'de': 'German (Deutsch)',
  'ja': 'Japanese (Êó•Êú¨Ë™û)'
};

const greetings = {
  'en': 'Hello! Your language preference has been saved.',
  'no': 'Hei! Spr√•kpreferansen din har blitt lagret.',
  'es': '¬°Hola! Su preferencia de idioma ha sido guardada.',
  'fr': 'Bonjour! Votre pr√©f√©rence linguistique a √©t√© enregistr√©e.',
  'de': 'Hallo! Ihre Spracheinstellung wurde gespeichert.',
  'ja': '„Åì„Çì„Å´„Å°„ÅØÔºÅË®ÄË™ûË®≠ÂÆö„Åå‰øùÂ≠ò„Åï„Çå„Åæ„Åó„Åü„ÄÇ'
};

function setCookie(name, value, days) {
  const date = new Date();
  date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
  const expires = 'expires=' + date.toUTCString();
  document.cookie = name + '=' + value + ';' + expires + ';path=/';
}

function getCookie(name) {
  const nameEQ = name + '=';
  const cookies = document.cookie.split(';');
  for (let i = 0; i < cookies.length; i++) {
    let cookie = cookies[i].trim();
    if (cookie.indexOf(nameEQ) === 0) {
      return cookie.substring(nameEQ.length);
    }
  }
  return null;
}

function updateCookieDisplay(lang) {
  if (lang) {
    const now = new Date();
    const expiry = new Date(now.getTime() + (365 * 24 * 60 * 60 * 1000));
    cookieValue.innerHTML = `
      <div style="padding: 12px; background-color: #f0f4ff; border-left: 4px solid #667eea; border-radius: 4px;">
        <p style="margin: 5px 0; font-family: monospace; font-size: 0.9em;">
          <strong>Name:</strong> preferred_language<br>
          <strong>Value:</strong> ${lang}<br>
          <strong>Expires:</strong> ${expiry.toLocaleDateString()}<br>
          <strong>Path:</strong> /
        </p>
      </div>
      <p style="margin-top: 10px; color: #667eea; font-style: italic;">${greetings[lang]}</p>
    `;
  } else {
    cookieValue.innerHTML = '<p style="color: #999; font-style: italic;">No cookie set - select a language to create one</p>';
  }
}

if (langSelect) {
  // Load saved preference on page load
  const savedLang = getCookie('preferred_language');
  if (savedLang) {
    langSelect.value = savedLang;
    updateCookieDisplay(savedLang);
  }

  // Automatically save when user changes language
  langSelect.addEventListener('change', function() {
    const selectedLang = this.value;
    setCookie('preferred_language', selectedLang, 365);
    updateCookieDisplay(selectedLang);

    // Visual feedback
    pageContent.style.transform = 'scale(0.98)';
    setTimeout(() => {
      pageContent.style.transform = 'scale(1)';
    }, 150);
  });
}

