// Scroll Position Tracker Example
const scrollYDisplay = componentRoot.querySelector('.scroll-y');
const scrollPercentDisplay = componentRoot.querySelector('.scroll-percent');
const scrollStatusDisplay = componentRoot.querySelector('.scroll-status');
const scrollProgressFill = componentRoot.querySelector('.scroll-progress-fill');
const scrollDemoContent = componentRoot.querySelector('.scroll-demo-content');

if (scrollDemoContent && scrollYDisplay) {
  scrollDemoContent.addEventListener('scroll', function() {
    const scrollTop = this.scrollTop;
    const scrollHeight = this.scrollHeight - this.clientHeight;
    const scrollPercent = (scrollTop / scrollHeight) * 100;

    // Update displays
    scrollYDisplay.textContent = Math.round(scrollTop);
    scrollPercentDisplay.textContent = Math.round(scrollPercent);
    scrollProgressFill.style.width = scrollPercent + '%';

    // Update status
    if (scrollTop === 0) {
      scrollStatusDisplay.textContent = 'At Top';
      scrollStatusDisplay.style.color = '#28a745';
    } else if (scrollTop >= scrollHeight - 5) {
      scrollStatusDisplay.textContent = 'At Bottom';
      scrollStatusDisplay.style.color = '#dc3545';
    } else {
      scrollStatusDisplay.textContent = 'Scrolling';
      scrollStatusDisplay.style.color = '#667eea';
    }
  });
}

