function displayResult(title, result, code) {
  funcOutput.innerHTML = `
    <div style="padding: 15px; background-color: #e7f3ff; border-radius: 5px; margin-top: 15px;">
      <h4 style="margin: 0 0 10px 0; color: #667eea;">${title}</h4>
      <p style="margin: 5px 0; font-size: 1.1em; color: #333;"><strong>Result:</strong> ${result}</p>
      <p style="margin: 10px 0 0 0; font-size: 0.85em; color: #666; font-family: monospace; background-color: #fff; padding: 8px; border-radius: 3px;">${code}</p>
    </div>
  `;
}

if (regularBtn && arrowBtn && paramBtn && returnBtn) {
  regularBtn.addEventListener('click', () => {
    const result = regularFunction();
    displayResult('Regular Function', result, 'function regularFunction() { return "Hello from a regular function!"; }');
  });

  arrowBtn.addEventListener('click', () => {
    const result = arrowFunction();
    displayResult('Arrow Function', result, 'const arrowFunction = () => { return "Hello from an arrow function!"; };');
  });

  paramBtn.addEventListener('click', () => {
    const result = greetUser(funcInput.value);
    displayResult('Function with Parameters', result, `greetUser("${funcInput.value || ''}")`)
  });

  returnBtn.addEventListener('click', () => {
    const result = addNumbers(5, 3);
    displayResult('Function Return Value', result, 'const addNumbers = (a, b) => a + b;\naddNumbers(5, 3)');
  });
}

