// Closure Example
const createCounterBtn = componentRoot.querySelector('.create-counter-btn');
const countersList = componentRoot.querySelector('.counters-list');

function createCounter() {
  let count = 0; // Private variable accessible only through closure

  return {
    increment() {
      count++;
      return count;
    },
    decrement() {
      count--;
      return count;
    },
    getCount() {
      return count;
    },
    reset() {
      count = 0;
      return count;
    }
  };
}

const counters = [];
let counterIdGenerator = 1;

function renderCounters() {
  if (counters.length === 0) {
    countersList.innerHTML = '<p style="color: #999; font-style: italic; padding: 20px; text-align: center;">Create a counter to see closures in action!</p>';
    return;
  }

  countersList.innerHTML = counters.map((item, index) => `
    <div style="padding: 15px; background-color: #f0f4ff; border-radius: 8px; margin-bottom: 12px; border: 2px solid #667eea;">
      <div style="display: flex; justify-content: space-between; align-items: center; margin-bottom: 10px;">
        <strong style="color: #667eea; font-size: 1.1em;">Counter #${item.id}</strong>
        <span style="font-size: 2em; font-weight: bold; color: #333;" id="count-${index}">${item.counter.getCount()}</span>
      </div>
      <div style="display: flex; gap: 8px; flex-wrap: wrap;">
        <button onclick="handleCounterAction(${index}, 'increment')" style="flex: 1; min-width: 80px; padding: 8px 12px; background-color: #28a745; color: white; border: none; border-radius: 5px; cursor: pointer; font-weight: 600;">+ Increment</button>
        <button onclick="handleCounterAction(${index}, 'decrement')" style="flex: 1; min-width: 80px; padding: 8px 12px; background-color: #dc3545; color: white; border: none; border-radius: 5px; cursor: pointer; font-weight: 600;">- Decrement</button>
        <button onclick="handleCounterAction(${index}, 'reset')" style="flex: 1; min-width: 80px; padding: 8px 12px; background-color: #6c757d; color: white; border: none; border-radius: 5px; cursor: pointer; font-weight: 600;">Reset</button>
      </div>
    </div>
  `).join('');
}

// Store in window for onclick access
window.handleCounterAction = function(index, action) {
  const counter = counters[index].counter;
  let newValue;

  if (action === 'increment') newValue = counter.increment();
  else if (action === 'decrement') newValue = counter.decrement();
  else if (action === 'reset') newValue = counter.reset();

  // Update display with animation
  const countElement = document.getElementById(`count-${index}`);
  if (countElement) {
    countElement.style.transform = 'scale(1.3)';
    countElement.textContent = newValue;
    setTimeout(() => {
      countElement.style.transform = 'scale(1)';
    }, 200);
  }
};

if (createCounterBtn) {
  createCounterBtn.addEventListener('click', () => {
    const newCounter = createCounter();
    counters.push({ id: counterIdGenerator++, counter: newCounter });
    renderCounters();
  });

  renderCounters();
}

